import { IWorldView } from "./IWorldView";
import { Snake } from "./Snake";

export class WorldModel {
  private _width: number;
  private _height: number;
  private _allSnakes: Snake[];
  private _allViews: IWorldView[];

  constructor(worldWidth: number, worldHeight: number) {
    this._width = worldHeight;
    this._height = worldHeight;
    this._allSnakes = [];
    this._allViews = [];
  }

  public addSnake(snake: Snake): void {
    this.allSnakes.push(snake);
  }

  public addView(view: IWorldView): void {
    this.allViews.push(view);
  }

  public update(steps: number): void {
    this.allSnakes.forEach((snake) => {
      snake.move(steps);
    });

    this.allViews.forEach((view) => {
      view.display(this);
    });
  }

  public get allSnakes(): Snake[] {
    return this.allSnakes;
  }

  public get width(): number {
    return this.worldWidth;
  }

  public get height(): number {
    return this.worldHeight;
  }
}
