import { IWorldView } from "./IWorldView";
import { Snake } from "./Snake";

export class WorldModel {
  private _allSnakes: Snake[];
  private _allViews: IWorldView[];

  constructor() {
    this._allSnakes = [];
    this._allViews = [];
  }

  public addSnake(snake: Snake): void {
    this.allSnakes.push(snake);
  }

  public addView(view: IWorldView): void {
    this._allViews.push(view);
  }

  public update(steps: number): void {
    // call move on each snake in allSnakes.
    this.allSnakes.forEach((snake) => {
      snake.move(steps);
    });
    // Update allviews
    this._allViews.forEach((view) => {
      view.display(this);
    });
  }

  public get allSnakes(): Snake[] {
    return this.allSnakes;
  }
}
